        IN date VARCHAR(20),
        IN limit1 INTEGER(11),
        IN limit2 INTEGER(11)
    )
    NOT DETERMINISTIC
    CONTAINS SQL
    SQL SECURITY DEFINER
    COMMENT ''
BEGIN
SET @date = date;
SET @limit1 = limit1;
SET @limit2 = limit2;  
  PREPARE STMT FROM "select * from news where date < ? ORDER BY news.ID DESC LIMIT ?, ?";
  EXECUTE STMT USING @date, @limit1, @limit2;
END;

